let score = 0;
let currentQuestions = [];

function loadQuiz() {
    const quizDiv = document.getElementById("quiz");
    quizDiv.innerHTML = "";
    
    // Reset current questions array
    currentQuestions = [...questions];
    
    currentQuestions.forEach((item, idx) => {
        let qBox = document.createElement("div");
        qBox.className = "question";
        qBox.innerHTML = `<b>${idx+1}. ${item.q}</b>`;

        item.o.forEach(opt => {
            let op = document.createElement("div");
            op.className = "option";
            op.textContent = opt;

            op.addEventListener("mouseover", () => {
                if(op.classList.contains("disabled")) return;
                op.classList.add(opt === item.a ? "correct" : "wrong");
            });

            op.addEventListener("mouseout", () => {
                op.classList.remove("correct", "wrong");
            });

            op.addEventListener("click", () => {
                if(op.classList.contains("disabled")) return;

                if(opt === item.a) {
                    score++;
                    op.classList.add("correct");
                } else {
                    op.classList.add("wrong");
                    // Highlight correct answer when wrong option is selected
                    Array.from(qBox.querySelectorAll(".option")).forEach(o => {
                        if(o.textContent === item.a) {
                            o.classList.add("correct");
                        }
                    });
                }

                // disable all options
                Array.from(qBox.querySelectorAll(".option")).forEach(o => {
                    o.classList.add("disabled");
                });

                document.getElementById("score").textContent = score;
            });

            qBox.appendChild(op);
        });

        quizDiv.appendChild(qBox);
    });
}

document.getElementById("resetBtn").addEventListener("click", () => {
    score = 0;
    document.getElementById("score").textContent = 0;
    loadQuiz();
});

// Initialize quiz on page load
document.addEventListener("DOMContentLoaded", loadQuiz);
